import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: 0,
      name: " ",
      number: " ",
      phoneBook: [{ name: " ", number: " " }],
    };

    this.nameInput = (e) => {
      this.setState({
        name: e.target.value,
      });
    };

    this.numberInput = (e) => {
      this.setState({
        number: e.target.value,
      });
    };

    this.addContact = () => {
      const newContact = {
        name: this.state.name,
        number: this.state.number,
      };

      if (this.state.name === "" || this.state.number === "") {
        alert("Both fields are required.");
        return;
      }

      this.setState((prevState) => ({
        phoneBook: prevState.phoneBook.concat(newContact),
        name: "",
        number: "",
      }));
    };

    this.showForm = () => {
      this.setState();
    };
  }

  componentDidMount() {
    this.loadLocalStorage();
    window.addEventListener("beforeunload", this.saveToLocalStorage.bind(this));
  }

  componentWillUnmount() {
    window.removeEventListener(
      "beforeunload",
      this.saveToLocalStorage.bind(this)
    );
    this.saveToLocalStorage();
  }

  loadLocalStorage() {
    for (let key in this.state) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  saveToLocalStorage() {
    for (let key in this.state) {
      localStorage.setItem(key, JSON.stringify(this.state[key]));
    }
  }

  updateInput(key, value) {
    this.setState({ [key]: value });
  }

  deleteItem(id) {
    const phoneBook = [...this.state.phoneBook];
    const updatedPhoneBook = phoneBook.filter(
      (phoneBook) => phoneBook.id !== id
    );
    this.setState({ list: updatedPhoneBook });
  }

  render() {
    let form = null;
    if (this.state) {
      form = (
        <div className="container">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              onChange={this.nameInput}
              value={this.state.name}
              placeholder="Enter your full name"
            />
          </div>
          <div className="form-group">
            <input
              type="tel"
              className="form-control"
              onChange={this.numberInput}
              value={this.state.number}
              placeholder="Enter your phone number"
            />
          </div>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.addContact}
          >
            Add new contact
          </button>
        </div>
      );
    }

    return (
      <div className="container">
        <div className="App">
          <h2 className="header">React PhoneBook:</h2>
          {form}

          {this.state.phoneBook.map((contact) => (
            <div>
              <h5 className="m-3">{contact.name}</h5>
              <p className="m-3">{contact.number}</p>
              <button
                className="btn btn-danger btn-sm m-3"
                onClick={() => this.deleteItem(contact.id)}
              >
                Delete
              </button>
              <hr />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
