import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      name: "",
      number: "",
      phoneBook: [{ name: "", number: "" }],
    };

    this.nameInput = (e) => {
      this.setState({
        name: e.target.value,
      });
    };

    this.numberInput = (e) => {
      this.setState({
        number: e.target.value,
      });
    };

    this.addContact = () => {
      const newContact = {
        name: this.state.name,
        number: this.state.number,
      };

      if (this.state.name === "" || this.state.number === "") {
        alert("Both fields are required.");
        return;
      }

      this.setState((prevState) => ({
        phoneBook: prevState.phoneBook.concat(newContact),
        name: "",
        number: "",
      }));
    };

    this.showForm = () => {
      this.setState();
    };
  }

  deleteItem(id) {
    const phoneBook = [...this.state.phoneBook];
    const updatedPhoneBook = phoneBook.filter((phoneBook) => list.id !== id);
    this.setState({ list: updatedPhoneBook });
  }


  render() {
    let form = null;
    if (this.state) {
      form = (
        <div className="container">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              onChange={this.nameInput}
              value={this.state.name}
              placeholder="Enter your full name"
            />
          </div>
          <div className="form-group">
            <input
              type="tel"
              className="form-control"
              onChange={this.numberInput}
              value={this.state.number}
              placeholder="Enter your phone number"
            />
          </div>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.addContact}
          >
            Add new contact
          </button>
        </div>
      );
    }

    return (
      <div className="container">
        <div className="App">
          <h2 className="header">React PhoneBook:</h2>
          {form}

          {this.state.phoneBook.map((contact) => (
            <div>
              <h5 className="m-3">{contact.name}</h5>
              <p className="m-3">{contact.number}</p>
              <button
                className="btn btn-danger btn-sm m-3"
                onClick={() => this.deleteItem(phoneBook.id)}
              >
                Delete
              </button>
              <hr />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
