import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      number: "",
      contacts: [],
    };
  }

  addContact = () => {
    const newContact = {
      name: this.state.name,
      number: this.state.number
    };

    if (this.state.name === "" || this.state.number === "") {
      alert("Both fields are required.");
      return;
    }
    this.setState({contacts: this.state.contacts.concat(newContact), name: ""});
  };

  deleteItem = (index) => {
    this.setState({ contact: this.state.contacts.splice(index) });
  };

  // deleteItem = (id) => {
  //   const updatedContact = this.state.contact.filter((contact) => {
  //     return contact.id === id;
  //   });
  //   this.setState({ todos: updatedContact });
  // };


