  //   this.saveToLocalStorage();
  // }

  // loadLocalStorage() {
  //   for (let key in this.state) {
  //     if (localStorage.hasOwnProperty(key)) {
  //       let value = localStorage.getItem(key);
  //       try {
  //         value = JSON.parse(value);
  //         this.setState({ [key]: value });
  //       } catch (e) {
  //         this.setState({ [key]: value });
  //       }
  //     }
  //   }
  // }

  // saveToLocalStorage() {
  //   for (let key in this.state) {
  //     localStorage.setItem(key, JSON.stringify(this.state[key]));
  //   }
  // }

  // updateInput(key, value) {
  //   this.setState({ [key]: value });
  // }

  // ------------------------------------------------------

  render() {
    const form = <div className="container">
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              onChange={(e) => this.setState({name: e.target.value})}
              value={this.state.name}
              placeholder="Enter your full name"
            />
          </div>
          <div className="form-group">
            <input
              type="tel"
              className="form-control"
              onChange={(e) => this.setState({number: e.target.value})}
              value={this.state.number}
              placeholder="Enter your phone number"
            />
          </div>
          <button
            type="button"
            className="btn btn-primary btn-add-contact mt-1 mb-3"
            onClick={this.addContact}
          >
            Add new contact
          </button>
          <hr className="add-contact-line" />
        </div>


    return (
      <div className="container">
        <div className="App">
          <h1 className="header mb-4">React PhoneBook:</h1>
          {form}

          {this.state.contacts.map((contact, index) => (
            <div className="grid-container" key={index}>
              <div>
                <span className="font-weight-bold">{contact.name}</span>
                <br />
                <span className="font-italic">{contact.number}</span>
              </div>
              <div>
                <button
                  className="btn-delete"
                  onClick={() => this.deleteItem(index)}
                >
                  <svg
                    width="1em"
                    height="1em"
                    viewBox="0 0 16 16"
                    className="bi bi-trash"
                    fill="currentColor"
                    xmlns="http://www.w3.org/2000/svg"
                  >
